mapper.select:
select t_sys_log.id as sys_log_id, t_sys_log.log_type as sys_log_log_type, t_sys_log.operate_user_id as sys_log_operate_user_id, t_sys_log.operate_time as sys_log_operate_time, t_sys_log.request_uri as sys_log_request_uri, t_sys_log.request_method as sys_log_request_method, t_sys_log.request_params as sys_log_request_params, t_sys_log.request_ip as sys_log_request_ip, t_sys_log.request_server_address as sys_log_request_server_address, t_sys_log.exception as sys_log_exception, t_sys_log.exception_info as sys_log_exception_info, t_sys_log.start_time as sys_log_start_time, t_sys_log.end_time as sys_log_end_time, t_sys_log.execute_time as sys_log_execute_time, t_sys_log.user_agent as sys_log_user_agent from t_sys_log
#{item}
#{item}
and t_sys_log.request_server_address || t_sys_log.request_uri || case when length(t_sys_log.request_params) != 0 then '?' || t_sys_log.request_params else '' end like '%'||'mail'||'%'
and t_sys_log.start_time between #{searchStartTime} and #{searchEndTime}
