mapper.select:
select t_b_tool_main.id
from t_b_tool_main
left join (select * from t_s_user where deleted = 0) as tsu on tsu.id = t_b_tool_main.author_id
left join (select * from t_s_user_info where deleted = 0) as tsui on tsui.user_id = t_b_tool_main.author_id
left join json_table(t_b_tool_main.keywords, '$[*]' columns (keyword varchar(50) path '$')) as tk on true

    t_b_tool_main.deleted = 0

        #{item}






                        and t_b_tool_main.name regexp #{searchValue}


                        and t_b_tool_main.name like concat('%', #{searchValue}, '%')






                        and t_b_tool_main.tool_id regexp #{searchValue}


                        and t_b_tool_main.tool_id like concat('%', #{searchValue}, '%')






                        and tsui.nickname regexp #{searchValue}


                        and tsui.nickname like concat('%', #{searchValue}, '%')






                        and tsu.username regexp #{searchValue}


                        and tsu.username like concat('%', #{searchValue}, '%')






                        and tk.keyword regexp #{searchValue}


                        and tk.keyword like concat('%', #{searchValue}, '%')






                        and ( t_b_tool_main.name regexp #{searchValue}
                        or t_b_tool_main.tool_id regexp #{searchValue}
                        or tsui.nickname regexp #{searchValue}
                        or tsu.username regexp #{searchValue}
                        or tk.keyword regexp #{searchValue} )


                        and ( t_b_tool_main.name like concat('%', #{searchValue}, '%')
                        or t_b_tool_main.tool_id like concat('%', #{searchValue}, '%')
                        or tsui.nickname like concat('%', #{searchValue}, '%')
                        or tsu.username like concat('%', #{searchValue}, '%')
                        or tk.keyword like concat('%', #{searchValue}, '%') )






order by t_b_tool_main.id desc

select t_b_tool_main.id as tool_id, t_b_tool_main.name as tool_name, t_b_tool_main.tool_id as tool_tool_id, t_b_tool_main.icon as tool_icon, t_b_tool_main.description as tool_description, t_b_tool_main.base_id as tool_base_id, t_b_tool_main.author_id as tool_author_id, t_b_tool_main.ver as tool_ver, t_b_tool_main.keywords as tool_keywords, t_b_tool_main.source_id as tool_source_id, t_b_tool_main.dist_id as tool_dist_id, t_b_tool_main.entry_point as tool_entry_point, t_b_tool_main.publish as tool_publish, t_b_tool_main.review as tool_review, t_b_tool_main.create_time as tool_create_time, t_b_tool_main.update_time as tool_update_time, t_b_tool_main.deleted as tool_deleted, t_b_tool_main.version as tool_version, tsu.id as user_id, tsu.username as user_username, tsui.id as user_info_id, tsui.nickname as user_info_nickname, tsui.avatar as user_info_avatar, tsui.email as user_info_email, tbtc.id as tool_category_id, tbtc.name as tool_category_name, tbtc.enable as tool_category_enable, tbtc.create_time as tool_category_create_time, tbtc.update_time as tool_category_update_time, tbtc.deleted as tool_category_deleted, tbtc.version as tool_category_version from t_b_tool_main left join (select * from t_s_user where deleted = 0) as tsu on tsu.id = t_b_tool_main.author_id left join (select * from t_s_user_info where deleted = 0) as tsui on tsui.user_id = t_b_tool_main.author_id left join (select * from t_r_tool_main_category where deleted = 0) as trtmc on t_b_tool_main.id = trtmc.tool_id left join (select * from t_b_tool_category where deleted = 0 and enable = 1) as tbtc on tbtc.id = trtmc.category_id
#{item}
#{item}
